import { cn } from "@/lib/utils";
import { Slot } from "@radix-ui/react-slot";

import "./shimmer.css";

export function Shimmer({
  className,
  children,
  shimmerDuration = 0.65,
  asChild = false,
  style,
  ...props
}: {
  className?: string;
  children: React.ReactNode;
  shimmerDuration?: number;
  asChild?: boolean;
  style?: React.CSSProperties;
} & React.ComponentProps<"div">) {
  const Comp = asChild ? Slot : "div";

  return (
    <Comp
      className={cn(
        "shimmer-effect [--shimmer-base:theme(colors.gray.900)] [--shimmer-highlight:theme(colors.gray.300)]",
        className
      )}
      style={{ animationDuration: `${shimmerDuration}s`, ...style }}
      {...props}
    >
      {children}
    </Comp>
  );
}

.shimmer-effect {
  background: linear-gradient(
    90deg,
    var(--shimmer-base) 10%,
    var(--shimmer-highlight) 55%,
    var(--shimmer-base) 80%
  );
  color: transparent;
  background-repeat: repeat-x;
  background-size: 200% 100%;
  -webkit-background-clip: text;
  background-clip: text;
  animation-name: shimmer-effectAnimation;
  animation-timing-function: linear;
  animation-iteration-count: infinite;
  display: inline-block;
  position: relative;
}

@keyframes shimmer-effectAnimation {
  0% {
    background-position: 150%;
  }

  to {
    background-position: -50%;
  }
}
